package com.Anurag.project.Api.BookStore.Entites;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.time.LocalDate;@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Entity@Table(name = "BookStore")public class Book {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    private String title;    private  String author;    private double price;    private double rating;    private LocalDate dateOfPublish;}